func SetupChromedp() (context.Context, context.CancelFunc) {
	opts := append(chromedp.DefaultExecAllocatorOptions[:],
		chromedp.Flag("headless", true),
	)

	// Create a context with the custom options
	ctx, cancel := chromedp.NewExecAllocator(context.Background(), opts...)
	// defer cancel()

	// Create a new context
	ctx, cancel = chromedp.NewContext(ctx)
	return ctx, cancel
}

func GetHtml(ctx context.Context, url string) string {
	// Navigate to the desired URL
	err := chromedp.Run(ctx,
		chromedp.Navigate(url),
	)
	if err != nil {
		log.Fatal(err)
	}

	// Evaluate JavaScript code to retrieve the outerHTML of the <body> element
	var bodyHTML string
	err = chromedp.Run(ctx,
		chromedp.Evaluate(`document.body.outerHTML`, &bodyHTML),
	)
	if err != nil {
		log.Fatal(err)
	}
	return bodyHTML
}

